mapper.update:
UPDATE contents 
SET price=#{price}, dc=#{dc}, saleprice=#{saleprice}, point=#{point}
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')

SELECT contentsno, adminno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point, r FROM ( SELECT contentsno, adminno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point, rownum as r FROM ( SELECT contentsno, adminno, cateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, price, dc, saleprice, point FROM contents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE contents
SET title=#{title}, content=#{content}, word=#{word}, 
      price=#{price}, dc=#{dc}, saleprice=#{saleprice}, point=#{point}, salecnt=#{salecnt}
WHERE contentsno = #{contentsno }

UPDATE contents
SET  file1=#{file1 }, file1saved=#{file1saved }, thumb1=#{thumb1 }, size1=#{size1 }
WHERE contentsno = #{contentsno }
